example.code:
$.fn.equalizeHeights = function() {
  var maxHeight = this.map(function( i, e ) {
    return $( e ).height();
  }).get();
  return this.height( Math.max.apply( this, maxHeight ) );
};

$( "input" ).click(function() {
  $( "div" ).equalizeHeights();
});

